	set(layer[video_table].scale, 0.7);
	,
	set(layer[video_table].scale, 1.0);
);

if(device.mobile,
	if(pressed, 
		sub(dy, mouse.stagey, sy); 
		add(ny, cy, dy);
		if(ny LT 15, 
			set(ny, 15)
		); 
		if(ny GT 168, 
			set(ny, 168)
		);
		copy(y, ny); 
		sub(ty, ny, 15);
		div(ty, 170); 
		sub(th, 15, layer[thumbnail_items].pixelheight); 
		mul(ty, th); 
		add(ty,0); 
		copy(layer[thumbnail_items].y, ty); 
		delayedcall(0,dragging()); 
	);
	,
	if(pressed, 
		sub(dy, mouse.stagey, sy); 
		add(ny, cy, dy);
		if(ny LT 15, 
			set(ny, 15)
		); 
		if(ny GT 314, 
			set(ny, 400)
		);
		copy(y, ny); 
		sub(ty, ny, 15);
		div(ty, 400); 
		sub(th, 15, layer[thumbnail_items].pixelheight); 
		mul(ty, th); 
		add(ty,0); 
		copy(layer[thumbnail_items].y, ty); 
		delayedcall(0,dragging()); 
	);
);

